import { Component, OnInit } from '@angular/core';
import { Store } from '@ngrx/store';
import { Observable, Subject, Subscription, takeUntil, timer } from 'rxjs';
import { CategoryGames } from 'src/app/models/categories-games';
import * as fromApp from '../../../ngrx-store/app.reducer';

@Component({
  selector: 'app-game-list',
  templateUrl: './game-list.component.html',
  styleUrls: ['./game-list.component.scss']
})
export class GameListComponent {

  public currentCategoriesGames$: Observable<CategoryGames | null> = this.store.select((state) => state.games.selectedCategoryGameItems);

  constructor(private store: Store<fromApp.AppState>) {
  }
}
