import { Component, OnChanges } from '@angular/core';
import { Observable } from 'rxjs';
import { CategoriesGames } from '../models/categories-games';
import { GamesFacadeService } from '../ngrx-store/games-facade.service';

@Component({
  selector: 'app-topBar',
  templateUrl: './top-bar.component.html',
  styleUrls: ['./top-bar.component.scss']
})
export class TopBarComponent implements OnChanges {

  public getAllCategoryGames$: Observable<CategoriesGames[]> = this.gamesFacadeService.getAllCategoryGames$;
  // public selectedCategory$: Observable<string> = this.gamesFacadeService.getSelectedCategory$;

  constructor(private gamesFacadeService: GamesFacadeService) { }

  ngOnChanges(): void {
    this.gamesFacadeService.selectCategory(window.location.pathname.split('/'));
  }

  // select(name: string): void{
  //   this.gamesFacadeService.selectCategory(name);
  // }
}
