import { Component, OnInit } from '@angular/core';
import { GamesFacadeService } from '../ngrx-store/games-facade.service';

@Component({
  selector: 'app-topBar',
  templateUrl: './top-bar.component.html',
  styleUrls: ['./top-bar.component.scss']
})
export class TopBarComponent implements OnInit {

  public selected: string | undefined | null;

  constructor(private gamesFacadeService: GamesFacadeService) { }

  ngOnInit(): void {
    this.gamesFacadeService.selectCategory(window.location.pathname.split('/'));
  }

  select(name: string): void{
    this.selected = name;
  }

}
